# Function to find similar words using fuzzy matching
def get_similar_words(input_word, words_list):
    return difflib.get_close_matches(input_word, [word['word'] for word in words_list], n=4)

# Step 2: Implement the main loop
def search_word():
    while True:
        user_input = input("Enter a word to search (or 'q' to quit): ").lower()
        if user_input == 'q':
            break

        # Step 3: Search for the word in the word_data dictionary
        if user_input[0] in word_data:
            words_with_def = word_data[user_input[0]]
            found_words = [word for word in words_with_def if word['word'] == user_input]
            if found_words:
                print(f"Word: {found_words[0]['word']}")
                print(f"Definition: {found_words[0]['definition']}")
                
                # Step 4: Provide recommended similar words
                similar_words = get_similar_words(user_input, words_with_def)
                random.shuffle(similar_words)  # Randomly shuffle the similar words
                print("Recommended similar words:")
                for word in similar_words[:3]:  # Select the first 3 words after shuffling
                    print(f"- {word}")
                
                # Allow the user to select a recommended word for further details
                selected_word = input("Enter a recommended word to see its definition (or press Enter to continue): ").lower()
                selected_word_def = next((item['definition'] for item in words_with_def if item['word'] == selected_word), None)
                if selected_word_def:
                    print(f"Definition of {selected_word}: {selected_word_def}")
            else:
                print("Word not found.")
        else:
            print("No words found for this character in the dictionary.")
        
if __name__ == "__main__":
    search_word()
